mapper.select:
/* getPagedItems */
select
idx, type, cid, title, title1, title2,
regdate, regdate1, regdate2, regid, stat, director, year
from items

    stat  != 'D'

        AND cid = #{cid}


        AND type = #{type}


        AND (title like CONCAT('%',#{title},'%') OR title1 like CONCAT('%',#{title},'%') )


order by regdate desc, idx desc
limit ${pageSize} offset ${offsetIdx}

/* getItemsByIdx */
select
    t.idx, t.type, t.cid, t.title, t.title1, t.title2,
    t.regdate, t.regdate1, t.regdate2, t.regid, t.stat, t.director, t.year,
    m.mtype, m.meta
from items t
/* LEFT JOIN items_metas_mapping tm on tm.idx = t.idx */
LEFT JOIN items_metas m on m.idx = t.idx
where t.idx = #{idx}

/* countItems */
select count(*)
from items

    stat  != 'D'

        AND cid = #{cid}


        AND type = #{type}


        AND (title like CONCAT('%',#{title},'%') OR title1 like CONCAT('%',#{title},'%') )

mapper.update:
/* uptItems */
update items
set
regid = #{regid}

    , title = #{title}


    , title1 = #{title1}


    , title2 = #{title2}


    , regdate2 = #{regdate2}


    , stat = #{stat}

where idx = #{idx}

mapper.select:
/* getMovieCine21ByTitle */
select movieNm as title, cineKeywords as content from movie_cine21
where title = #{title}
order by idx DESC
limit 1

/* getCollectSchedId */
select sc3.sc_id
from sched_trigger sc3
where sc3.type = 'C' and sc3.stat = 'S' and sc3.sc_id in (
select sc2.parent_sc_id as sc_id
from sched_trigger sc2
where sc2.type = 'R' and sc2.stat = 'S' and sc2.sc_id in (
select sc.parent_sc_id as sc_id
from sched_trigger sc
where sc.type = 'A' and sc.stat = 'S' and sc.sc_id = #{sc_id}
)
)

/* getCollectContentList */
select stc.sc_id, stc.content
from sched_target_content stc
inner join sched_trigger sc on sc.sc_id = stc.sc_id and sc.type = 'C' and sc.stat = 'S'
where stc.sc_id = #{sc_id}

mapper.insert:
/* insYjItemsOut */
insert into yj_items_out (idx, yj_id, type, purity_title, regdate, stat)
values (#{idx}, #{cid}, #{type}, #{title}, now(), 'Y')

/* insYjItemsOut2 */
insert into yj_items_out2 (yj_id, type, purity_title, regdate, stat, title2)
values (#{yj_id}, 'yc1', #{title}, now(), 'Y', #{title2})

/* insYjTagsMetas */
insert into yj_tags_metas (title, title2, yj_id, mtype, meta, regdate)
values
(#{title}, #{title2}, #{yj_id}, #{mtype}, #{meta}, now());

mapper.select:
/* getYjidForInsert */
select yj_id as cid from yj_items
where
title2 = #{title2}
and yj_id not in ( select yj_id from yj_items_out2 )
limit 1

/* searchItemsPaging 191125 x3 */ select a.idx , CASE WHEN (cs.series_id is not null and cs.series_id != '0') THEN 'SER' ELSE ( select CASE WHEN (it.type = 'CcubeContentK') THEN 'KOR' ELSE 'OTH' END AS type ) END AS type , it.regdate1 as regdate , IFNULL( IFNULL(cc.CONTENT_TITLE,cs.PURITY_TITLE),title) as title, it.regdate as procdate, it.regid, it.director, it.year, it.tagcnt, it.duration , a.stat ,ifnull(cs.series_id, '0') as series_id ,ifnull(cc.master_content_id,'0') as content_id ,ifnull(cs.series_id, '0') as sorigin ,ifnull(cc.content_id,'0') as corigin from ( select j.* from ( (
select p.* from (
select it.idx, iss.stat, it.regdate
, case when (it.type = 'CcubeContent' or it.type = 'CcubeContentK') then cc.content_id ELSE cs.series_id END AS cid
from items it inner join items_stat iss on iss.idx = it.idx and iss.stat not in ('RC', 'RR', 'RA', 'SC', 'SR', 'CT')
and iss.stat = #{searchStat}
and iss.stat in ('FR', 'FA')
inner join ( select distinct(idx) as idx from items_metas where mtype in
#{item1}
and meta like concat('%',#{searchKeyword},'%') ) imm on imm.idx = it.idx
left outer join ( select t.* from ( select tk.idx, tk.tagidx , ( select max(tagidx) from items_tags_keys where idx = tk.idx ) as maxTagIdx from items_tags_keys tk where tk.stat = 'S' ) t where t.tagidx = t.maxTagIdx ) itk on itk.idx = it.idx
inner join ( select distinct(idx) as idx, tagidx from items_tags_metas where mtype in
#{item2}
and meta like concat('%',#{searchKeyword},'%') ) itm on itm.idx = it.idx and itm.tagidx = itk.tagidx
left outer join ( select distinct(content_id) from ccube_contents where actors_display like concat('%',#{searchKeyword},'%') ) cc on cc.content_id = it.cid left outer join ( select distinct(series_id) from ccube_series where actors_display like concat('%',#{searchKeyword},'%') ) cs on cs.series_id = it.cid
inner join ccube_keys ck on ck.itemidx = it.idx and (it.cid = ck.content_id or it.cid = ck.series_id) where it.regdate1 between concat(#{searchSdate}, ' 00:00:00') and concat(#{searchEdate}, ' 23:59:59') and it.stat != 'M'
and it.type = 'CcubeContentK'
and it.type = 'CcubeContent'
and it.type = 'CcubeSeries'
and it.director like CONCAT('%', #{searchKeyword} '%')
) p where p.cid is not null order by p.regdate desc, p.idx desc
order by it.regdate1 desc, it.idx desc
)
) j order by j.regdate desc, j.idx desc ) a inner join items it on it.idx = a.idx left outer join ccube_contents cc on cc.content_id = it.cid left outer join ccube_series cs on cs.series_id = it.cid
where IFNULL( IFNULL(cc.CONTENT_TITLE,cs.PURITY_TITLE),title) like CONCAT('%',#{searchKeyword},'%')
order by a.regdate desc, a.idx desc limit ${pageSize} offset ${offsetIdx}

