select p.* from (
select it.idx, iss.stat, iss.regdate
, case when (it.type = 'CcubeContent' or it.type = 'CcubeContentK') then cc.content_id ELSE cs.series_id END AS cid
from items it inner join items_stat iss on iss.idx = it.idx and iss.stat not in ('RC', 'RR', 'RA', 'SC', 'SR', 'CT')
and iss.stat = #{searchStat}
and iss.stat in ('FR', 'FA')
left outer join ( select distinct(content_id) from ccube_contents where actors_display like concat('%',#{searchKeyword},'%') ) cc on cc.content_id = it.cid left outer join ( select distinct(series_id) from ccube_series where actors_display like concat('%',#{searchKeyword},'%') ) cs on cs.series_id = it.cid
inner join ccube_keys ck on ck.itemidx = it.idx and (it.cid = ck.content_id or it.cid = ck.series_id) where iss.regdate between concat(#{searchSdate}, ' 00:00:00') and concat(#{searchEdate}, ' 23:59:59')
order by iss.regdate desc, iss.idx desc
)
) j order by j.regdate desc, j.idx desc ) a inner join items it on it.idx = a.idx left outer join ccube_contents cc on cc.content_id = it.cid left outer join ccube_series cs on cs.series_id = it.cid inner join items_metas im on im.idx = a.idx order by a.regdate desc, a.idx desc limit ${pageSize} offset ${offsetIdx}

