and actors_display like concat('%',#{item.searchKeyword},'%')
) cc on cc.content_id = it.cid left outer join ( select distinct(series_id) from ccube_series where 1=1
and actors_display like concat('%',#{item.searchKeyword},'%')
) cs on cs.series_id = it.cid
