and it.type = 'CcubeContentK'
and it.type = 'CcubeContent'
and it.type = 'CcubeSeries'
and it.director like CONCAT('%', #{item.searchKeyword} '%')
) p where p.cid is not null order by p.regdate desc, p.idx desc
order by it.regdate1 desc, it.idx desc
)
) j order by j.regdate desc, j.idx desc ) a inner join items it on it.idx = a.idx left outer join ccube_contents cc on cc.content_id = it.cid left outer join ccube_series cs on cs.series_id = it.cid
where 1=1
and IFNULL( IFNULL(cc.CONTENT_TITLE,cs.PURITY_TITLE),title) like CONCAT('%',#{item.searchKeyword},'%')

