/* countItemsPaging */ select count(*) as cnt from ( select j.* from ( (
select p.* from (
select it.idx, iss.stat, it.regdate
, case when (it.type = 'CcubeContent' or it.type = 'CcubeContentK') then cc.content_id ELSE cs.series_id END AS cid
from items it inner join items_stat iss on iss.idx = it.idx and iss.stat not in ('RC', 'RR', 'RA', 'SC', 'SR', 'CT')
and iss.stat = #{searchStat}
and iss.stat in ('FR', 'FA')
inner join ( select distinct(idx) as idx from items_metas where mtype in
#{item1}
and meta like concat('%',#{searchKeyword},'%') ) imm on imm.idx = it.idx
left outer join ( select t.* from ( select tk.idx, tk.tagidx , ( select max(tagidx) from items_tags_keys where idx = tk.idx ) as maxTagIdx from items_tags_keys tk where tk.stat = 'S' ) t where t.tagidx = t.maxTagIdx ) itk on itk.idx = it.idx
inner join ( select distinct(idx) as idx, tagidx from items_tags_metas where mtype in
#{item2}
and meta like concat('%',#{searchKeyword},'%') ) itm on itm.idx = it.idx and itm.tagidx = itk.tagidx
left outer join ( select distinct(content_id) from ccube_contents where actors_display like concat('%',#{searchKeyword},'%') ) cc on cc.content_id = it.cid left outer join ( select distinct(series_id) from ccube_series where actors_display like concat('%',#{searchKeyword},'%') ) cs on cs.series_id = it.cid
inner join ccube_keys ck on ck.itemidx = it.idx and (it.cid = ck.content_id or it.cid = ck.series_id) where it.regdate1 >= #{searchSdate} and it.regdate1 <= #{searchEdate} and it.stat != 'M'
and it.type = 'CcubeContentK'
and it.type = 'CcubeContent'
and it.type = 'CcubeSeries'
and it.director like CONCAT('%', #{searchKeyword} '%')
) p where p.cid is not null
)
) j ) a inner join items it on it.idx = a.idx left outer join ccube_contents cc on cc.content_id = it.cid left outer join ccube_series cs on cs.series_id = it.cid
where IFNULL( IFNULL(cc.CONTENT_TITLE,cs.PURITY_TITLE),title) like CONCAT('%',#{searchKeyword},'%')

/* countItemsAll */
select count(*) from items it
inner join ccube_keys ck on ck.itemidx = it.idx and (it.cid = ck.content_id or it.cid = ck.series_id)
where it.stat = 'Y';

